export default {
  // props:['icon','iconPosition'] //这个组件的props接受2个外部属性
  props:{
    icon:{},
    loading:{
      //给g-button一个loading值，类型为布尔，默认值是false
      type: Boolean,
      default: false,
    },
    iconPosition:{
      type: String, //接受的值是字符串
      default: 'left', //默认是left
    }
  }
};
